<template>
    <div>
        <div class="main" :style="{'height':options.height || '300px'}">
            <div class="wrap">
                <div>
                    <img src="data:image/png;base64,"
                         alt="">
                </div>
                <div class="text" v-el:content>
                    <span>{{{options.content}}}</span>
                </div>
            </div>
        </div>
    </div>
</template>
<script>
    /***
     *
     * @options
     *    height 高度
     *    content 提示文字（可以写html，会被编译）
     */
    import fragmentMixin from 'iview-biz/mixins/fragmentMixin'

    export default {
        mixins: [fragmentMixin],

        mock: mockData(),

        watch: {
            'options.content': function () {
                this.$compile(this.$els.content)
            }
        },

        ready(){
            this.$compile(this.$els.content)
        }

    }

    function mockData() {
        return {
            options: {
                height: '200px',
                content: '提示文字'
            }
        }
    }
</script>
<style scoped>
    .main {
        width: 100%;
        text-align: center;
        position: relative;
        display: table;
    }

    .wrap {
        width: 100%;
        vertical-align: middle;
        display: table-cell;
        margin: auto;
    }

    .text {
        margin-top: 8px;
    }

</style>
